<template>
  <section class="content-wrapp" @click="hideListOptions($event); indexAgent = 'false'" v-click-outside="hide"
           ref="agentMap">
    <div class="toolbar">
      <div>
        <div @click="switchViews()" class="btn-img">
          <svg v-if="!isList" content="List mode"
               v-tippy="{ placement : 'top',  arrow: true, delay : 800, duration: 500 }"
               class="switch-view switch-list"
               width="23" height="16" viewBox="0 0 19 15" fill="none" xmlns="http://www.w3.org/2000/svg">
            <rect x="4.76953" y="0.5" width="14.2305" height="1.76923" rx="0.884615"/>
            <rect x="4.76953" y="6.26953" width="14.2305" height="1.76923" rx="0.884615"/>
            <rect x="4.76953" y="12.0391" width="14.2305" height="1.76923" rx="0.884615"/>
            <circle cx="1.5" cy="1.5" r="1.5"/>
            <circle cx="1.5" cy="7.19922" r="1.5"/>
            <circle cx="1.5" cy="13" r="1.5"/>
          </svg>
          <svg content="Card mode" v-tippy="{ placement : 'top',  arrow: true, delay : 800, duration: 500 }" v-else
               class="switch-view"
               width="27" height="27" viewBox="32 27 27 27" fill="none" xmlns="http://www.w3.org/2000/svg">
            <rect x="32" y="27" width="11" height="11" rx="1"/>
            <rect x="48" y="27" width="11" height="11" rx="1"/>
            <rect x="32" y="43" width="11" height="11" rx="1"/>
            <rect x="48" y="43" width="11" height="11" rx="1"/>
          </svg>
        </div>
        <div class="top-btn">
          <div v-if="!isEdit" class="edit-map">
            <img content="Edit mode"
                 v-tippy="{ placement : 'top',  arrow: true, delay : 800, duration: 500 }"
                 src="@/assets/img/icons/edit-map.svg" @click="editMap()"/>
          </div>
          <div class="mini-card ">
            <img content="Reset" v-tippy="{ placement : 'top',  arrow: true, delay : 800, duration: 500 }"
                 src="@/assets/img/icons/to-back-map.svg" @click="toBackMap()"/>
          </div>
          <div v-if="isEdit" class="edit-map">
            <img content="Apply changes" v-tippy="{ placement : 'top',  arrow: true, delay : 800, duration: 500 }"
                 src="@/assets/img/icons/go-back-map.svg" @click="isEdit = !isEdit; updateMap()"
                 class="update"/>
          </div>
          <div v-if="isEdit" class="add-card">
            <img content="Add card" v-tippy="{ placement : 'top',  arrow: true, delay : 800, duration: 500 }"
                 src="@/assets/img/icons/add-agent-map.svg" @click="addCard()"/>
          </div>
        </div>
        <ul class="tabs-sort" v-if="!isList">
          <li class="toolbar-text">Sort by:</li>
          <li @click="indexAgent = 'true'" :ref="'true'">
            <label class="options-label">
              <input class="input-field num" type="text" :value="sortBy === 'internalNumber' ? 'Agent number' : sortBy"
                     readonly>
              <span @click="sortByShow = !sortByShow" class="triangle" :class="{'rotate-arrow': sortByShow}">
                <svg width="12" height="7" viewBox="0 0 12 7" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path opacity="0.9" d="M1 1L6 6L11 1" stroke-linecap="round" stroke-linejoin="round"/>
                </svg>
              </span>
              <ul v-show="sortByShow" class="list-related-numbers">
                <li @click="toBackMap('Custom')">Custom
                  <svg v-show="sortBy === 'Custom'" aria-hidden="true" focusable="false" data-prefix="fas"
                       data-icon="star" class="svg-inline--fa fa-star fa-w-18"
                       role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 576 512">
                    <path fill="red"
                          d="M259.3 17.8L194 150.2 47.9 171.5c-26.2 3.8-36.7 36.1-17.7 54.6l105.7 103-25 145.5c-4.5 26.3 23.2 46 46.4 33.7L288 439.6l130.7 68.7c23.2 12.2 50.9-7.4 46.4-33.7l-25-145.5 105.7-103c19-18.5 8.5-50.8-17.7-54.6L382 150.2 316.7 17.8c-11.7-23.6-45.6-23.9-57.4 0z"></path>
                  </svg>
                </li>
                <li @click="sort('internalNumber')">Agent number
                  <sort-direction :show="sortBy === 'internalNumber'" :is-desc="sortDirection"/>
                </li>
                <li @click="sort('name')">Name
                  <sort-direction :show="sortBy === 'name'" :is-desc="sortDirection"/>
                </li>
                <li @click="sort('location')">Location
                  <sort-direction :show="sortBy === 'location'" :is-desc="sortDirection"/>
                </li>
                <li @click="sort('status')">Time in status
                  <sort-direction :show="sortBy === 'status'" :is-desc="sortDirection"/>
                </li>
                <li @click="sort('talkingTo')">Talking to
                  <sort-direction :show="sortBy === 'talkingTo'" :is-desc="sortDirection"/>
                </li>
              </ul>
            </label>
          </li>
        </ul>
      </div>
    </div>

    <section class="list-header-block" v-if="isList">
      <ul class="list-header">
        <li class="list-header-item header-name" @click="sort('name')">
          Name
          <img src="@/assets/img/icons/sort-arrow.svg" ref="name"/>
        </li>
        <li class="list-header-item header-number" @click="sort('internalNumber')">
          Agent number
          <img src="@/assets/img/icons/sort-arrow.svg" ref="internalNumber"/>
        </li>
        <li class="list-header-item header-location" @click="sort('location')">
          Location
          <img src="@/assets/img/icons/sort-arrow.svg" ref="location"/>
        </li>
        <li class="list-header-item header-time" @click="sort('status')">
          Time in status
          <img src="@/assets/img/icons/sort-arrow.svg" ref="status"/>
        </li>
        <li class="list-header-item header-talking" @click="sort('talkingTo')">
          Talking to
          <img src="@/assets/img/icons/sort-arrow.svg" ref="talkingTo"/>
        </li>
        <li class="list-header-item header-actions" @click="sort('actions')">
          Actions
          <img src="@/assets/img/icons/sort-arrow.svg" ref="actions"/>
        </li>
        <li></li>
      </ul>
    </section>

    <div class="drag-row">
      <div style="width: 100%">
        <div class="list" v-if="usersAgentMap.length">
          <drop-list :items="usersAgentMap" @reorder="$event.apply(usersAgentMap)" mode="cut"
                     :class="{'drop-list-list': isList}">
            <template v-slot:item="{item, reorder}">
              <drag :key="item.placeInList" :data="item" @cut="remove(usersAgentMap, item)"
                    :style="!isEdit ? 'pointer-events: none' : null"
                    :class="{'dnd-tremor': isEdit && !isList, 'dnd-tremor-list': isEdit && isList}">
                <div class="item-content" v-if="!isList"
                     :style="reorder ? {borderLeft: '2px solid #1976D2', marginLeft:'-2px'} : {}"
                     :class="{'item-not-user-edit': item.notUser && isEdit, 'item-edit-user': !item.notUser && isEdit, 'item-not-user' : item.notUser }">
                  <span class="pointer-status" :style="{background: displayStatusWeb(item).color}"
                        v-if="!item.notUser"></span>
                  <span class="pointers" @click="showMenu(item); indexAgent = item.uid" :ref="item.uid"
                        v-if="!item.notUser">
                    <img src="../../assets/img/icons/agent-map-points.svg"/>
                  </span>
                  <span class="days" :style="{background: displayStatusPhone(item).color}"
                        v-if="!item.notUser && item.timeToView.days"
                        content="Days"
                        v-tippy="{ placement : 'top',  arrow: true, delay : 800, duration: 500 }">
                    <span class="circle">
                      {{ item.timeToView.days }}
                      {{ console.log('item:',item) }}
                    </span>
                  </span>
                  <span class="delete-card" v-if="isEdit && item.notUser">
                    <button @click="deleteCard(item)" class="btn-del">
                      <img src="@/assets/img/icons/cross-delete.svg">
                    </button>
                  </span>
                  <div class="top-content" v-if="!item.notUser">
                    <div class="avatar">
                      <img :src="item.avatar || defaultAva" alt="user-avatar"/>
                    </div>
                    <div class="top-text">
                      <span class="name ellipsis">{{ item.name }}</span>
                      <span class="agent-number">Agent number: {{ item.internalNumber }}</span>
                      <span class="location ellipsis">{{ item.location || '' }}</span>
                    </div>
                  </div>
                  <div class="item-time-status" v-if="!item.notUser">
                    <div class="item-time" :style="{color: displayStatusPhone(item).color}">
                      {{ item.timeToView.time }}
                      <img :src="displayStatusPhone(item).icon" alt="phone" class="status-phone"/>
                    </div>
                    <div class="item-status" :style="{color: displayStatusPhone(item).color}">
                      <span class="statusText">
                        <span class="dote-status" :style="{background: displayStatusPhone(item).color}"></span>
                        {{ !allowedStatuses.includes(item.status) ? item.status : item.statusPhone.statusText }}
                        {{ item.status === 'onpause' || item.status === 'paused' && item.statusPhone.statusText === 'online' ? '(P)' : '' }}
                      </span>
                      <span class="talkingTo">{{ item.talkingTo }}</span>
                    </div>
                  </div>
                  <div class="buttons" :class="{active: getStatus(item) === 'talking'}" v-if="!item.notUser">
                    <span class="btn" @click="spy(item)">SPY</span>
                    <span class="btn" @click="whisper(item)">WHISPER</span>
                    <span class="btn" @click="merge(item)">MERGE</span>
                  </div>
                  <div class="vertical-menu" v-show="isShowMenu === item.uid"
                       @click="showMenu(item); indexAgent = item.uid" :ref="item.uid">
                    <img src="../../assets/img/icons/agent-map-points.svg"/>
                  </div>
                  <div class="content-menu" v-show="isShowMenu === item.uid" :ref="item.uid"
