<script>
import { Drag, DropList } from 'vue-easy-dnd'
import SortDirection from '@/components/sort-direction'
import ava from '@/assets/img/icons/user_icon.svg'
import OnlineIcon from '@/assets/img/icons/OnlineIcon.svg'
import PauseIcon from '@/assets/img/icons/PauseIcon.svg'
import OfflineIcon from '@/assets/img/icons/OfflineIcon.svg'
import TalkingIcon from '@/assets/img/icons/TalkingIcon.svg'
import BlockedIcon from '@/assets/img/icons/blocked-status.svg'
import UnreachableIcon from '@/assets/img/icons/UnreachableIcon.svg'
import CallingIcon from '@/assets/img/icons/CallingIcon.svg'
import RingingIcon from '@/assets/img/icons/RingingIcon.svg'
import logOut from '@/assets/img/icons/menu-logout-agentsMap.svg'
import logIn from '@/assets/img/icons/log-in.svg'
import ClickOutside from 'vue-click-outside'
import { clickCheck } from '@/lib/clickCheck.js'
import { allowedStatuses } from '@/store/modules/UserDashboard'
import _ from 'lodash'
import { mapGetters } from 'vuex'


export default {
  components: {
    SortDirection,
    Drag,
    DropList
  },
  data() {
    return {
      allowedStatuses,
      defaultAva: ava,
      OnlineIcon,
      PauseIcon,
      OfflineIcon,
      TalkingIcon,
      RingingIcon,
      UnreachableIcon,
      CallingIcon,
      BlockedIcon,
      filter: {
        name: '',
        view: ''
      },
      sortParam: {
        name: 'desc',
        internalNumber: 'desc',
        location: 'desc',
        status: 'desc',
        talkingTo: 'desc',
        actions: 'desc',
        Custom: 'desc'
      },
      isList: false,
      isShowMenu: '',
      isEdit: false,
      initialData: [],
      sortByShow: false,
      sortBy: 'Custom',
      logOut: logOut,
      logIn: logIn,
      indexAgent: '',
    }
  },
  computed: {
    ...mapGetters(['usersAgentMap']),
    sortDirection() {
      return this.sortParam[this.sortBy] === 'desc'
    }
  },
  methods: {
    async fetchData() {
      // Create the WebSocket connection
      this.ws = new WebSocket(`${window.env.WS_URL}/agent-map`, localStorage.getItem('token'));

      // Handle incoming messages
      this.ws.onmessage = (event) => {
        console.log('WebSocket message received:', event.data); // Logging raw data
        try {
          const parsedData = JSON.parse(event.data);
          //console.log('Status:', parsedData.data.statusText); // Logging raw data
          console.log('Parsed data:', parsedData.user); // Logging parsed data
          // Assuming `parsedData` has a `data` property that is an array of objects
          const users = parsedData.user;
          Object.keys(users).forEach((key) => {
            const user = users[key];
            console.log('user is: ', user);
            this.rows.push({
              AgentName: user.name,
              Role: user.permission.role,
              Status: user.status,
              Duration: "7.41", // Replace with actual duration if available
              AVGTalkingTime: "2m 3s", // Replace with actual talking time if available
              Calls: 91, // Replace with actual call count if available
            });
          });
        } catch (e) {
          this.errorMessage = 'Error parsing data.';
          console.error('Error parsing data:', e);
        }
      };

      // Handle errors
      this.ws.onerror = (error) => {
        this.errorMessage = 'WebSocket error: ' + error.message;
      };

      // Handle connection close
      this.ws.onclose = (event) => {
        if (event.wasClean) {
          console.log('Connection closed cleanly');
        } else {
          this.errorMessage = 'Connection closed abruptly';
        }
      };
    },
    sortTable(key, tableType) {
      if (tableType === 'waiting') {
        this.sortKeyWaiting = key;
        this.sortAscWaiting = !this.sortAscWaiting;
      } else {
        this.sortKeyNotWaiting = key;
        this.sortAscNotWaiting = !this.sortAscNotWaiting;
      }
    },
    sortAndFilter(rows, sortKey, sortAsc) {
      return rows.slice().sort((a, b) => {
        if (sortKey) {
          const aVal = a[sortKey];
          const bVal = b[sortKey];
          if (aVal < bVal) return sortAsc ? -1 : 1;
          if (aVal > bVal) return sortAsc ? 1 : -1;
        }
        return 0;
      });
    },



    spy(item) {
      this.$store.commit('showPhoneMutation', true)
      this.$store.dispatch('sipCall', `1*${item.internalNumber}`)
    },
    whisper(item) {
      this.$store.commit('showPhoneMutation', true)
      this.$store.dispatch('sipCall', `2*${item.internalNumber}`)
    },
    merge() {
      this.$store.commit('showPhoneMutation', true)
      this.$store.dispatch('sipCall', `3*${item.internalNumber}`)
    },
    hideListOptions() {
      clickCheck(this, this.indexAgent, this.$refs)
    },
    hide() {
      this.isShowMenu = ''
      this.sortByShow = false
    },
    sort(param) {
      if (!this.initialData.length) {
        this.initialData = JSON.parse(JSON.stringify(this.usersAgentMap))
      }
      const fill = this.usersAgentMap.filter(el => !el.notUser)
      const empty = this.usersAgentMap.filter(el => el.notUser)
      const fillSort = _.orderBy(fill, [param], [this.sortParam[param]])
      this.$store.commit('usersAgentMapCreate', fillSort.concat(empty))
      if (this.sortParam[param] === 'desc') {
        this.sortParam[param] = 'asc'
        this.$refs[param] ? this.$refs[param].style.transform = 'rotate(180deg)' : null
      } else {
        this.sortParam[param] = 'desc'
        this.$refs[param] ? this.$refs[param].style.transform = '' : null
      }
      this.sortBy = param
    },
    editMap() {
      this.initialData = JSON.parse(JSON.stringify(this.usersAgentMap))
      this.isEdit = true
    },
    addCard() {
      this.$store.commit('addEmptySlot')
    },
    toBackMap(sort = '') {
      if (sort) this.sortBy = 'Custom'
      if (this.initialData.length) {
        this.$store.commit('usersAgentMapCreate', this.initialData)
      }
      this.isEdit = false
    },
    deleteCard(user) {
      this.$store.commit('userAgentMapDelete', user)
    },
    setPause(user) {
      this.$store.dispatch('userOnPauseEmit', {uid: user.uid, pause: user.onPause ? 0 : 1})
    },
    logOutUser(user) {
      this.$store.dispatch('userLogoutEmit', {...user})
      this.isShowMenu = ''
    },
    updateMap() {
      this.initialData = []
      let obj = {}
      this.usersAgentMap.forEach((item, index) => {
        if (!item.notUser) {
          obj[index] = item.uid
        }
      })
      this.$store.dispatch('savePositionsOnDashboard', obj)
      this.isEdit = false
    },
    showMenu(value) {
      if (this.isShowMenu === value.uid) {
        this.isShowMenu = ''
      } else {
        this.isShowMenu = value.uid
      }
    },
    remove(array, value) {
      let index = array.indexOf(value)
      array.splice(index, 1)
    },
    switchViews() {
      this.isList = !this.isList
    },
    displayStatusWeb(item) {
      switch (item.status) {
        case 'online':
          return {color: '#00C82C', icon: this.OnlineIcon}
        case 'paused':
          return {color: '#FFB200', icon: this.PauseIcon}
        case 'onpause':
          return {color: '#FFB200', icon: this.PauseIcon}
        case 'offline':
          return {color: '#FF3A29', icon: this.OfflineIcon}
        default:
          return {color: '#FF3A29', icon: this.OfflineIcon}
      }
    },
    getStatus(item) {
      return !this.allowedStatuses.includes(item.status) ? item.status : item.statusPhone.statusText
    },
    displayStatusPhone(item) {
      const status = !this.allowedStatuses.includes(item.status) ? item.status : item.statusPhone.statusText
      switch (status) {
        case 'online':
          return {color: '#00C82C', icon: this.OnlineIcon}
        case 'offline':
          return {color: '#FF3A29', icon: this.OfflineIcon}
        case 'calling':
          return {color: '#78ABCE', icon: this.CallingIcon}
        case 'ringing':
          return {color: '#78ABCE', icon: this.RingingIcon}
        case 'talking':
          return {color: '#276899', icon: this.TalkingIcon}
        case 'blocked':
          return {color: '#636D75', icon: this.BlockedIcon}
        default:
          if (status && status.indexOf('&') !== -1) {
            return {color: '#78ABCE', icon: this.CallingIcon}
          } else return {color: '#636D75', icon: this.UnreachableIcon}
      }
    },
  },
  mounted() {
    this.popupItem = this.$refs.agentMap
    this.$store.commit('resetAgentMap')
    this.$store.dispatch('startConnectionAgentMap')
  },
  beforeDestroy() {
    this.$store.dispatch('closeConnectionAgentMap')
  },
  directives: {
    ClickOutside,
  },
}
</script>
