<script>
import VueSlickCarousel from 'vue-slick-carousel'
import 'vue-slick-carousel/dist/vue-slick-carousel-theme.css'
export default {
  name: 'PhoneBottomMenu',
  props: ['currentComponent', 'setCurrentComponentProp', 'goToIndex', 'show'],
  components: {
    VueSlickCarousel
  },
  data() {
    return {
      settings: {
        "centerMode": true,
        "speed": 500,
        "dots": false,
        "arrows": false,
        "variableWidth": true,
        "swipe": false,

        "focusOnSelect": true,
        "infinite": true,
        "slidesToShow": 3.5,
        "speed": 500
      },
      slideIndex: 0,
      firstRender: true
    }
  },
  watch: {
    goToIndex() {
      if(this.goToIndex === null) return
      const indexToGo = this.slideIndex >= 10 ? this.goToIndex + 10 : this.slideIndex >= 5 ? this.goToIndex + 5 : this.goToIndex
      this.$refs.carousel.goTo(indexToGo)
    },
    show() {
      this.setFirstRender()
    }
  },
  methods: {
    setCurrentComponent(value) {
      this.$emit('setCurrentComponentProp', value)
    },
    beforeChangeHandler() {
      this.setCurrentComponent('')
    },
    clickHandler(index) {
      const currentIndex = this.slideIndex >= 10 ? this.slideIndex - 10 : this.slideIndex >= 5 ? this.slideIndex - 5 : this.slideIndex
      index === currentIndex ? this.afterChangeHandler(index) : null
    },
    afterChangeHandler(e) {
      const index = e >= 10 ? e - 10 : e >=5 ? e - 5 : e
      switch (index) {
        case 0:
          this.setCurrentComponent('recentCalls')
          break
        case 1:
          this.setCurrentComponent('contacts')
          break
        case 2:
          this.setCurrentComponent('keyboard')
          break
        case 3:
          this.setCurrentComponent('callMenu')
          break
        case 4:
          this.setCurrentComponent('settings')
          break
      
        default:
          break
      }
      this.slideIndex = e
    },
    setFirstRender() {
      if(this.show) {
        this.firstRender = false
      }
    }
  },
  mounted() {
    this.setFirstRender()
  }
}
</script>
